def _undict_value(value):
    if isinstance(value, dict):
        return value['value']
    else:
        return value


# Returns a proper NULL value when the API returns 'null' string
def _null_value(value):
    if json.dumps(value) == 'null':
        return None
    else:
        return json.dumps(value)


def _save_campaign(campaign):
    record = {
        'contentful_id': campaign['contentful_id'],
        'internal_title': campaign['internal_title'],
        'slug': campaign['slug'],
        'title': campaign['title'],
        'legacy_campaign_id': campaign['legacy_campaign_id'],
    }
    query = ''.join(("INSERT INTO public.contentful_data ( "
                     "contentful_id,internal_title, slug, title, legacy_campaign_id)"
                     "VALUES (:contentful_id,:internal_title,"
                     ":slug,:title,:legacy_campaign_id) "))
    db.query_str(query, record)


def process_json(data):
    campaign = {}
    for record in data["entries"]:
        if record['sys']['id']:
            campaign['contentful_id'] = record['sys']['id']

        if record['fields']["internalTitle"]["en-US"]:
            campaign['internal_title'] = record['fields']["internalTitle"]["en-US"]

        if record['fields']["slug"]["en-US"]:
            campaign['slug'] = record['fields']["slug"]["en-US"]
        
        if record['fields']['title']['en-US']:
            campaign['title'] = record['fields']['title']['en-US']

        if 'legacyCampaignId' in record['fields']:
            campaign['legacy_campaign_id'] = record['fields']['legacyCampaignId']['en-US']
        else:
            campaign['legacy_campaign_id'] = 0
        
        _save_campaign(campaign)

